package com.synrgy.commit.util;

import com.synrgy.commit.controller.piksi.SosialisasiSekolahController;
import com.synrgy.commit.dao.request.EmailSenderModel;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Qualifier;
import org.springframework.core.task.TaskExecutor;
import org.springframework.scheduling.annotation.Scheduled;
import org.springframework.stereotype.Component;
import org.springframework.stereotype.Service;

import java.util.Date;


@Service
@Component
public class CronJobSendEmail {

    @Autowired //backround [proccses
    @Qualifier(value = "taskExecutor")
    private TaskExecutor taskExecutor;

    @Autowired
    private SosialisasiSekolahController op;

    @Scheduled(cron = "${cron.expression:-}")
    public void sendAsync() {
        taskExecutor.execute(new Runnable() {
            @Override
            public void run() {
                System.out.println("Method Email send start :: " + new Date());
                EmailSenderModel p = new EmailSenderModel();
                p.setPengirim("poltekpis@gmail.com");
                p.setSubjek("Kesempatan Kuliah di Politeknik Piksi Input Serang");
                p.setKontent("");
                op.sendEmailPassword(p);
                System.out.println("Method Email send selesai :: " + new Date());
            }
        });
    }
}